import React, {useEffect, useState} from 'react';
import {Switch, Route} from 'react-router-dom';
import axios from 'axios';
import {Layout} from 'antd';
// import { Typography } from '@mui/material';

// import GlobalStyle, { Container } from '../globalStyles';
import Bg from '../components/background';
import Searching from '../components/searching';
import Result from '../components/result';
import MapContainer from '../components/cafe/Map'
import Content from '../components/content';
import Cafe_Info
    from './info/Cafe_info';

const {Header} = Layout;

// const { Title } = Typography;
function Main(props) {
    const bg_i = document.getElementById("background");
    const images = ["0.jpg", "1.jpg", "2.jpg", "3.jpg", "4.jpg", "5.jpg", "6.png"];
    const chosenImage = images[Math.floor(Math.random() * images.length)]
    const paintImage = (chosenImage) => {
        // const image = new Image();
        const image = document.createElement("img");
        image.src = require("../assets/img/"+ `${chosenImage}`).default;
        // image.classList.add("bgImage");
        bg_i.style.backgroundImage=image.src;
    };
    paintImage(chosenImage);
    return (
        <div id="background">

            <Switch>
                <Route exact path="/info/:id"><Cafe_Info/></Route>

                <Route exact path='/search/:gu/:place' render={() => <Result/>}/>

                <Route path="/" render={() => <Searching/>}/>
                {/* <Route render={() => <div className='error'>에러 페이지</div>} /> */}
                <Content/>
            </Switch>


        </div>

    )
}

export default Main;
