const bg_i = document.getElementById("root");
const images = ["0.jpg", "1.jpg", "2.jpg", "3.jpg", "4.jpg", "5.jpg", "6.png"];
const chosenImage = images[Math.floor(Math.random() * images.length)]
const paintImage = (chosenImage) => {
    // const image = new Image();
    const image = document.createElement("img");
    image.src = require("../assets/img/${chosenImage}").default;
    // image.classList.add("bgImage");
    bg_i.appendChild(image);
};
paintImage(chosenImage);

const bg = document.querySelector(".random_BG");

const bgList = [
    "../assets/img/0.jpg", "../assets/img/1.jpg", "../assets/img/2.jpg", "../assets/img/3.jpg", "../assets/img/4.jpg", "../assets/img/5.jpg", "../assets/img/6.png"
];

const randNum = Math.floor(Math.random() * bgList.length);

bg.style.backgroundImage = `url(${bgList[randNum]})`;
