import React, {useEffect, useState} from 'react';
import {Switch, Route} from 'react-router-dom';
import axios from 'axios';
import {Layout} from 'antd';
// import { Typography } from '@mui/material';

// import GlobalStyle, { Container } from '../globalStyles';
import Bg from '../components/background';
import Searching from '../components/searching';
import Result from '../components/result';
import MapContainer from '../components/cafe/Map'
import Content from '../components/content';
import Cafe_Info
    from './info/Cafe_info';
import styled from "styled-components";

const {Header} = Layout;
const Container = styled.div`
    background: ${props=>props.backgroundImage};
    margin: 0 auto;
    background-repeat: no-repeat;
    background-size: cover;
    opacity: 0.8;
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    z-index: -1;
`;

// const { Title } = Typography;
function Main(backgroundImage) {
    const IMAGES = ["../assets/img/0.jpg", "../assets/img/1.jpg", "../assets/img/2.jpg", "../assets/img/3.jpg", "../assets/img/4.jpg", "../assets/img/5.jpg", "../assets/img/6.png"]
    const IMG_NUMBER = 6;
    const getRandom = () => {
        return Math.floor(Math.random() * IMG_NUMBER)
    }
    const paintImage = (imgNumber) => {
        const backgroundImage = "url('" + IMAGES[imgNumber] + "')"
        console.log(backgroundImage);
        // document.body.style.backgroundImage = "url('../assets/img/0.jpg')";
    }

    return (
        <div>
            <Container backgroundImage={backgroundImage}></Container>
            <Switch>
                <Route exact path="/info/:id"><Cafe_Info/></Route>

                <Route exact path='/search/:gu/:place' render={() => <Result/>}/>

                <Route path="/" render={() => <Searching/>}/>
                {/* <Route render={() => <div className='error'>에러 페이지</div>} /> */}
                <Content/>
            </Switch>


        </div>

    )
}

export default Main;
